// server.js
import express from 'express';
import routes from './routes/index'; // Import your main router
import dbClient from './utils/db'; // Import dbClient to ensure DB connection is initialized
import redisClient from './utils/redis'; // Import redisClient to ensure Redis connection is initialized

const app = express();
const PORT = process.env.PORT || 5000; // Use environment variable PORT or default to 5000

// Middleware to parse JSON request bodies
// This is essential for req.body to contain the JSON data from POST requests.
app.use(express.json());

// Load all routes from routes/index.js
// This line uses the 'routes' router imported above.
app.use('/', routes);

// Ensure DB and Redis connections are ready before starting the server
(async () => {
  await dbClient.connect(); // Connect to MongoDB
  // Redis client is initialized in its constructor, no explicit connect call needed here for `redis` package.

  // Check initial connection status (optional, but good for debugging)
  if (!dbClient.isAlive()) {
    console.error("MongoDB connection failed on startup.");
    // Optionally, exit the process or handle more gracefully
  }
  if (!redisClient.isAlive()) {
    console.warn("Redis connection failed on startup. API might have limited functionality.");
  }

  // Start the Express server
  app.listen(PORT, () => {
    console.log(`Server running on port ${PORT}`);
  });
})();

export default app; // Export the app for testing or other uses

